mapper.select:
  		SELECT COUNT(*)
		FROM LIKES
		WHERE USER_ID = #{userId}
		AND STYLE_NO = #{styleNo}

mapper.delete:
  		DELETE FROM LIKES
		WHERE USER_ID = #{userId}
		AND STYLE_NO = #{styleNo}

mapper.select:
	    SELECT REPLY_NO, REPLY_WRITER, REPLY_CONTENT, TO_CHAR(REPLY_DATE, 'YYYY-MM-DD') AS "REPLY_DATE", STATUS, STYLE_NO
      FROM REPLY
      WHERE STYLE_NO = #{sno}
