mapper.select:
SELECT COUNT(*) COUNT
FROM(
	SELECT BOARD_NO
	FROM USEDBOARD
	WHERE STATUS = 'Y'

		AND PRODUCT_TYPE_NAME = #{productTypeName}


		AND BRANDNAME = #{brandName}


		AND COLLECTION_NAME = #{collectionName}


	)

SELECT *
FROM(SELECT ROWNUM RNUM, A.*
    FROM(
	    SELECT BOARD_NO, BOARD_TITLE, USED_PRICE, FILE_PATH||CHANGE_NAME TITLEIMG
	    FROM USEDBOARD U
	    JOIN ATTACHMENT ON(BOARD_NO = REF_NO)
	    WHERE U.STATUS = 'Y' AND FILE_LEVEL = 1 AND TYPE_NO = 2

			AND PRODUCT_TYPE_NAME = #{productTypeName}


			AND BRANDNAME = #{brandName}


			AND COLLECTION_NAME = #{collectionName}

		) A)
WHERE RNUM BETWEEN #{startRow} AND #{endRow}

ORDER BY RNUM

mapper.update:
UPDATE USEDBOARD
SET STATUS = 'N'
WHERE BOARD_NO = #{boardNo}
